userDefinedLinkHintCss:
div > .vimiumHintMarker { /* linkhint boxes */
  background: #3B4252; /* -webkit-gradient(linear, left top, left bottom, color-stop(0%,#FFF785),
    color-stop(100%,#FFC542)) */
  border: 1px solid #3B4252; /* #E3BE23 */
}

div > .vimiumHintMarker span { /* linkhint text */
  color: white; /* black */
  font-weight: normal; /* bold */
  font-size: 12px;
}

